mapper.select:
  	SELECT *
  	FROM(
	  	SELECT ROWNUM AS "RN", post_no,post_title, post_content
	  		, TO_CHAR(reg_date, '""YYYY"년 "MM" "DD"일"') AS "reg_date", recommend, lookup
		            ,category_name, user_id, category_no
		FROM(
		    SELECT *
		    FROM v_board_post
		    WHERE category_no = #{category_no}
		    ORDER BY post_no DESC
		)
	)WHERE RN BETWEEN #{beginList} AND #{endList}

  	SELECT post_no, post_title, post_content, reg_date, recommend, lookup
        ,category_name, user_id, category_no
	FROM v_board_post
	WHERE category_no = #{category_no}
	AND post_no = #{post_no}

mapper.delete:
DELETE FROM board_post
WHERE CATEGORY_NO = ${category_no}
AND POST_NO = #{post_no}
